    <Route
      exact={exact}
      path={path}
      render={(props) => {
        if (isLoggedIn) return <Redirect to="/private" />
        else if (!isLoggedIn) return <Component {...props} />
      }} />
  )
}

export default withAuth(PublicRoute);
